:root {
  --font-family: Inter, sans-serif;
  --text-color: DarkSlateGray;
  --font-size: 12px;
  --padding: 10px 5px;
  --height: 35px;
  --border-color: DarkSlateGray;
}

@page {
  padding-top: 25mm;
