.tg, .no-border {
  border-collapse: collapse;
  width: 100%;
  font-family: var(--font-family);
  color: var(--text-color);
}
.tg td, .tg th, .no-border td, .no-border th {
  font-size: var(--font-size);
  text-align: left;
  vertical-align: top;
  padding: var(--padding);
  word-break: normal;
  height: var(--height);
}

.tc td, .tc th, .no-border td, .no-border th {
  font-size: var(--font-size);
  text-align: left;
  vertical-align: top;
  padding: var(--padding);
  word-break: normal;
  height: var(--height);
}

.tg td, .tg th {
  border: 1px solid var(--border-color);
}
.tg .col-20, .no-border .col-20 {
  width: 20%;
  /* 20% width for the first four columns */
}
.tg .col-10, .no-border .col-10 {
  width: 10%;
  /* 10% width for the last two columns */
}
.tg .col-24, .no-border .col-24 {
  width: 24%;
  /* 20% width for the first four columns */
}
.tg .col-12, .no-border .col-12 {
  width: 15%;
  /* 10% width for the last two columns */
}
.tg .col-18, .no-border .col-18 {
  width: 18%;
  /* 10% width for the last two columns */
}
.break {
  page-break-before: always;
}
/* Custom styles for the second table */
.tg.break tbody tr:first-child td, .tg.break tbody tr:first-child th {
  border-top: none;
  border-left: none;
  border-right: none;
}
.tg.break tbody tr:not(:first-child) th {
  vertical-align: middle !important;
  text-align: center;
}
.tg.break tbody tr:not(:first-child) td:nth-child(2), .tg.break tbody tr:not(:first-child) td:nth-child(4) {
  border-left: none;
}
/* New class for tables without borders */
.no-border td, .no-border th {
  border: none;
  height: 10px;
  padding: 5px !important;
}

